var cloudjs = { 
	history: '',
	version: '0.5',
	body_cont: '#cloud-body',
	default_page: 'home',
	error_page: '/templates/page_not_found',
	current_url: '',
	uri: {},
	focus: '',
	api: { search_clear: 'Search', search_filter: /[^a-z0-9\s_-]/ig, url_filters: {} },
	templates: { html: {}, cont: {}}
}

//
// Functions we call on page load.
//
cloudjs.init = function ()
{
	this.history_linking();
	this.api.init();
} 

// ----------------- Deep Linking ----------------- //

//
// Setup deep linking (or history linking or html5 push state).
// Tons of terms all meaning the same thing. We default to 
// hashes if the browser does not support html5 push state.
//
cloudjs.history_linking = function ()
{
	cloudjs.history = window.History;

	// This is called when the website state changes.
	cloudjs.history.Adapter.bind(window, 'statechange', function() { 	
		var state = cloudjs.history.getState();
		
		// Load page into the body of the cloudjs.
		$(cloudjs.body_cont).load(state.cleanUrl, function(response, status, xhr) {
			// Grab the 404 error body html.
			if(status == "error") 
			{
				$(cloudjs.body_cont).load(cloudjs.error_page);
			}
			
			// See if this is a full redirect (no more ajax, reload page).
			if(response.match('cloudjs-redirect:')) 
			{ 
				var url = $(response).text().replace('cloudjs-redirect:', '');
				window.location = url;
				return;
			}
			
			// Set the focus. 
			if(cloudjs.focus.length > 0)
			{
				$('#' + cloudjs.focus).focus();
				cloudjs.focus = '';
			}
			
			// After page load call init functions.
			$(window).scrollTop(0);
			cloudjs.history_after_load(state);
			cloudjs.api.refresh_get();
		});
	});

	// Setup click event for anchors.
	$('a').live('click', function() { 
		// Make sure this link it meant to be deep linked.
		if($(this).hasClass('no-deep-true')) { return true; }
		if($(this).hasClass('no-deep-false')) { return false; }	
		if($(this).attr('href') == '#') { return false; }
		if($(this).attr('href') == 'javascript:void(null);') { return false; }

		cloudjs.history_click($(this));
		cloudjs.current_url = $(this).attr('href').replace('#', '');
		cloudjs.history.pushState('', cloudjs.page_title, cloudjs.current_url);
		
		return false;
	});
}

//
// Call on deep link click.
//
cloudjs.history_click = function (that)
{
	return true;
} 

//
// Call after page load.
//
cloudjs.history_after_load = function (state)
{
	return true;
}
//
// Clear all API filters.
//
cloudjs.api.clear_filter = function()
{
	cloudjs.api.url_filters = {}
}

//
// Set a custom API filter.
//
cloudjs.api.set_filter = function (name, value)
{
	cloudjs.api.url_filters[name] = value;	
}

//
// Api init. We call this when the api library first loads.
//
cloudjs.api.init = function ()
{
	// Load data.
	cloudjs.api.refresh_get();

	// Bind to delete clicks.
	$('[cloud-api-delete]').live('click', function() {
		var url = $(this).attr('href');
		var id = $(this).attr('cloud-api-delete').split(':')[0];
		var action = $(this).attr('cloud-api-delete').split(':')[1];
		var conyes = $(this).attr('cloud-api-delete').split(':')[2];
		var parm1 = $(this).attr('cloud-api-delete').split(':')[3];
		var parm2 = $(this).attr('cloud-api-delete').split(':')[4];
		
		// Show a confirm dialog.
		if(conyes == 'confirm')
		{
			var c = confirm('Are you sure you want to delete this entry?');
			if(! c)
			{
				return false;
			}
		}
		
		// Figure out how to remove this.
		switch(action)
		{
			case 'remove-fade':
				$(this).closest(parm1).fadeOut(parm2, function () { $(this).remove(); });
			break;
		}
		
		// Make the API call to delete this.
		$.post(url, {Id: id});
		
		return false;
	});
}

//
// Loop through the dom and update api data.
//
cloudjs.api.refresh_get = function ()
{
	$('[cloud-api-url]').each(function () {
		var data = {
			cont: $(this),
			tmpl: $(this).attr('cloud-tmpl-cont'),
			url: $(this).attr('cloud-api-url'),
			entry: $(this).attr('cloud-api-entry'),
			limit: $(this).attr('cloud-api-limit'),
			offset: $(this).attr('cloud-api-offset'),
			start: $(this).attr('cloud-api-start-num'),
			end: $(this).attr('cloud-api-end-num'),
			total: $(this).attr('cloud-api-total-num'),
			filtered: $(this).attr('cloud-api-filtered-num'),
			filtered_string: $(this).attr('cloud-api-filtered-string')
		}

		// Add the search param.
		if($(this).attr('cloud-api-search') !== undefined)
		{
			var fid = $(this).attr('cloud-api-search');
			var term = $('#' + fid).val().replace(cloudjs.api.search_filter, '');
			
			if((cloudjs.api.search_clear.length > 0) && (term != cloudjs.api.search_clear))
			{
				data.url = data.url.replace(/{{search}}/ig, term);
			} else
			{
				data.url = data.url.replace(/{{search}}/ig, '');
			}
		}
		
		// Set up order if they passed it in.
		if($(this).attr('cloud-api-order') !== undefined)
		{
			var order = $(this).attr('cloud-api-order');
			data.url = data.url.replace(/{{order}}/ig, order);
		} else
		{
			data.url.replace(/{{order}}/ig, '');
		}
		
		// Set up sort if they passed it in.
		if($(this).attr('cloud-api-sort') !== undefined)
		{
			var val = $(this).attr('cloud-api-sort');
			data.url = data.url.replace(/{{sort}}/ig, val);
		} else
		{
			data.url.replace(/{{sort}}/ig, '');
		}
		
		// Set up offset if they passed it in.
		if($(this).attr('cloud-api-offset') !== undefined)
		{
			var val = $(this).attr('cloud-api-offset');
			data.url = data.url.replace(/{{offset}}/ig, val);
		} else
		{
			data.url.replace(/{{offset}}/ig, '');
		}
		
		// Set up limit if they passed it in.
		if($(this).attr('cloud-api-limit') !== undefined)
		{
			var val = $(this).attr('cloud-api-limit');
			data.url = data.url.replace(/{{limit}}/ig, val);
		} else
		{
			data.url.replace(/{{limit}}/ig, '');
		}
		
		// Load up any custom filters.
		$.each(cloudjs.api.url_filters, function(key, row) {
			var re = new RegExp("{{" + key + "}}", 'ig');
			if(row.length > 0)
			{
				data.url = data.url.replace(re, row);			
			} else
			{
				data.url = data.url.replace(re, '');
			}
		});
		
		data = cloudjs.api.before_api(data);
		cloudjs.api.get(data);
	});
}

//
// Manage get data requests.
//
cloudjs.api.get = function (data)
{
	// Load variables. 
	cloudjs.templates.load(data.tmpl, data.cont);

	// Send api request to get data and load a template.	
	$.get(data.url, function (json) {
		if(json.status)
		{
			if(data.entry == 'true')
			{
				$.each(json.data, function (index, row) {
					if(typeof row === 'object')
					{
						$.each(row, function (index2, row2) {
							if(typeof row2 === 'string')
							{
								$('[cloud-api-field="' + index + '"] [cloud-api-field="' + index2 + '"]').html(row2);
							}
						});
					} else
					{
						$('[cloud-api-field="' + index + '"]').html(row);
					}
				});				
			} else
			{
				cloudjs.templates.cont_empty(data.tmpl);
				$.each(json.data, function (index, row) {
					cloudjs.templates.print(data.tmpl, row);
				});
				
				// --------------- Set paging vars -------------- //
				
				// Set start cont.
				if(typeof data.offset != 'undefined')
				{
					// We add a one because most databases start with 0 with offsets.
					$('.' + data.start).html(parseInt(data.offset) + 1);
				}
				
				// Set end cont.
				if((typeof data.limit != 'undefined') && (typeof data.offset != 'undefined'))
				{
					if(data.limit == json.data.length)
					{
						$('.' + data.end).html(parseInt(data.limit) + parseInt(data.offset));
					} else
					{
						$('.' + data.end).html(parseInt(json.data.length) + parseInt(data.offset));						
					}
				}
				
				// Set total results cont.
				if((typeof data.total != 'undefined') && (typeof json.total != 'undefined'))
				{
					$('.' + data.total).html(json.total);
				}
				
				// Set filtered cont.
				if((typeof data.filtered != 'undefined') && (typeof json.filtered != 'undefined'))
				{
					$('.' + data.filtered).html(json.filtered);
				}
				
				// See if we should hide the filtered string or not.
				if((typeof data.filtered_string != 'undefined') && (typeof json.filtered != 'undefined') && (typeof json.total != 'undefined') && (json.filtered == json.total))
				{
					$('.' + data.filtered_string).hide();
				} else
				{
					$('.' + data.filtered_string).show();					
				}
				
				// ----------------- End Paging vars --------------- //
			}
		}
		
		cloudjs.api.after_api(json);
	}, 'json');
	
	return true;
}

//
// Call on deep link click.
//
cloudjs.api.before_api = function (data)
{
	return data;
}
//
// Call on deep link click.
//
cloudjs.api.after_api = function (json)
{
	return json;
}

//
// Return a url segment by index.
//
cloudjs.uri.segment = function (index)
{
	var segs = window.location.pathname.split('/');
	if(typeof segs[index] == "undefined")
	{
		return '';
	} else
	{
		return segs[index];
	}
}
//
// Load a template into memory. We pass a dom id.
//
cloudjs.templates.load = function (id, cont)
{
	this.cont[id] = cont;
	this.html[id] = $('#' + id).html();
}

//
// Pass data into the template and print it to the dom.
//
cloudjs.templates.print = function(id, data)
{
	var html = this.html[id];
	
	$.each(data, function (index, row) {
		html = html.replace(new RegExp('{{' + index + '}}', 'g'), row);	
	});
	
	this.cont[id].append(html);
}

//
// Empty a container.
//
cloudjs.templates.cont_empty = function (id)
{
	this.cont[id].html('');
}

